import React, { useState } from 'react';
import {
  Box,
  VStack,
  HStack,
  FormControl,
  FormLabel,
  Input,
  Select,
  Button,
  Text,
  useToast,
  Heading,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  StatGroup,
} from '@chakra-ui/react';

interface TradeForm {
  sourcePlanet: string;
  destinationPlanet: string;
  goods: string;
  quantity: number;
  paymentType: 'upfront' | 'on_delivery';
}

// Planet and star system data
// Distances are stored in AU (1 light year = 63241.1 AU)
const PLANET_DISTANCES: Record<string, number> = {
  // Solar System
  Mercury: 0.387,
  Venus: 0.723,
  Earth: 1,
  Mars: 1.524,
  Jupiter: 5.203,
  Saturn: 9.537,
  Uranus: 19.191,
  Neptune: 30.069,

  // Nearby Stars and Their Planets
  'Proxima Centauri b': 268725.8,  // 4.25 light years
  'Alpha Centauri A': 271909.7,    // 4.3 light years
  'Alpha Centauri B': 271909.7,    // 4.3 light years
  'Barnard\'s Star': 379446.6,     // 6.0 light years
  'Wolf 359': 474308.3,            // 7.5 light years
  'Lalande 21185': 515521.8,       // 8.15 light years
  'Sirius A': 537549.4,            // 8.5 light years
  'Sirius B': 537549.4,            // 8.5 light years

  // Notable Exoplanets
  'TRAPPIST-1d': 2529644.0,       // 40 light years
  'TRAPPIST-1e': 2529644.0,       // 40 light years
  'TRAPPIST-1f': 2529644.0,       // 40 light years
  'Kepler-186f': 3099814.0,       // 49 light years
  'TOI-700d': 6324110.0,          // 100 light years

  // Notable Star Systems
  'Betelgeuse': 39525687.5,       // 625 light years
  'Vega': 158102750.0,            // 2500 light years
  'Antares': 316205500.0,         // 5000 light years
  'Deneb': 474308250.0,           // 7500 light years

  // Galactic Features
  'Orion Nebula': 853754850.0,    // 13,500 light years
  'Galactic Center': 1580275000.0, // 25,000 light years
  'LMC': 3162055000.0,            // 50,000 light years (Large Magellanic Cloud)
};

// Update the interface to include descriptions
interface TradeForm {
  sourcePlanet: string;
  destinationPlanet: string;
  goods: string;
  quantity: number;
  paymentType: 'upfront' | 'on_delivery';
}

// Add planet descriptions
const CELESTIAL_BODY_INFO: Record<string, string> = {
  Mercury: 'Innermost planet of the Solar System',
  Venus: 'Second planet from the Sun, similar in size to Earth',
  Earth: 'Our home planet',
  Mars: 'The Red Planet, target for human colonization',
  Jupiter: 'Largest planet in the Solar System',
  Saturn: 'Known for its spectacular ring system',
  Uranus: 'Ice giant with unique sideways rotation',
  Neptune: 'The windiest planet in the Solar System',
  'Proxima Centauri b': 'Closest known exoplanet to Earth, potentially habitable',
  'Alpha Centauri A': 'Part of the closest star system to Earth',
  'Alpha Centauri B': 'Binary companion to Alpha Centauri A',
  'Barnard\'s Star': 'One of the closest stars to Earth',
  'Wolf 359': 'Small red dwarf star in Leo constellation',
  'Lalande 21185': 'Red dwarf with confirmed exoplanets',
  'Sirius A': 'Brightest star in Earth\'s night sky',
  'Sirius B': 'White dwarf companion to Sirius A',
  'TRAPPIST-1d': 'Part of the TRAPPIST-1 system of seven Earth-like planets',
  'TRAPPIST-1e': 'Most promising TRAPPIST-1 planet for habitability',
  'TRAPPIST-1f': 'Water-rich world in the TRAPPIST-1 system',
  'Kepler-186f': 'First Earth-sized planet discovered in habitable zone',
  'TOI-700d': 'Earth-sized planet in its star\'s habitable zone',
  'Betelgeuse': 'Red supergiant star, could go supernova soon',
  'Vega': 'Bright star and former North Star',
  'Antares': 'Red supergiant star in Scorpius',
  'Deneb': 'One of the most luminous known stars',
  'Orion Nebula': 'Closest massive star formation region to Earth',
  'Galactic Center': 'Supermassive black hole at the heart of the Milky Way',
  'LMC': 'Largest satellite galaxy of the Milky Way'
};

export const TradePanel: React.FC = () => {
  const [form, setForm] = useState<TradeForm>({
    sourcePlanet: 'Earth',
    destinationPlanet: 'Mars',
    goods: '',
    quantity: 0,
    paymentType: 'upfront',
  });

  const [calculation, setCalculation] = useState<{
    distance: number;
    travelTime: number;
    shipTime: number;
    interestRate: number;
    totalCost: number;
    timeDilation: number;
  } | null>(null);

  const toast = useToast();

  const calculateTradeParameters = (distance: number, paymentType: 'upfront' | 'on_delivery') => {
    // Constants
    const SPEED_OF_LIGHT = 299792458; // m/s
    const SHIP_SPEED_RATIO = 0.9; // 90% of light speed
    const SHIP_SPEED = SPEED_OF_LIGHT * SHIP_SPEED_RATIO;
    const AU_TO_METERS = 149597870700; // 1 AU in meters
    const SECONDS_PER_DAY = 24 * 60 * 60;
    const SECONDS_PER_YEAR = 365.25 * SECONDS_PER_DAY;

    // Calculate distance in meters
    const distanceMeters = distance * AU_TO_METERS;

    // Calculate time dilation factor (γ - gamma)
    // γ = 1 / sqrt(1 - v²/c²)
    const timeDilationFactor = 1 / Math.sqrt(1 - (SHIP_SPEED_RATIO ** 2));

    // Calculate travel times
    // Earth time (coordinate time) = distance / ship_speed
    const earthTimeSeconds = distanceMeters / SHIP_SPEED;

    // Ship time (proper time) = Earth time / γ
    const shipTimeSeconds = earthTimeSeconds / timeDilationFactor;

    // Convert to days for display
    const earthTimeDays = earthTimeSeconds / SECONDS_PER_DAY;
    const shipTimeDays = shipTimeSeconds / SECONDS_PER_DAY;

    // Calculate years for interest rate calculation
    const earthTimeYears = earthTimeSeconds / SECONDS_PER_YEAR;
    const shipTimeYears = shipTimeSeconds / SECONDS_PER_YEAR;

    // Base annual interest rate (5%)
    const BASE_INTEREST_RATE = 0.05;

    // Calculate interest rates based on Krugman's paper
    let effectiveInterestRate;
    if (paymentType === 'upfront') {
      // For upfront payment, use Earth time since the money could have been invested on Earth
      effectiveInterestRate = (1 + BASE_INTEREST_RATE) ** earthTimeYears - 1;
    } else {
      // For payment on delivery, account for both time dilation and the fact that
      // payment is delayed in both reference frames
      effectiveInterestRate = (1 + BASE_INTEREST_RATE) ** (earthTimeYears * timeDilationFactor) - 1;
    }

    return {
      distance,
      travelTime: earthTimeDays,
      shipTime: shipTimeDays,
      timeDilation: timeDilationFactor,
      interestRate: effectiveInterestRate,
    };
  };

  const handleSubmit = () => {
    const sourceDistance = PLANET_DISTANCES[form.sourcePlanet];
    const destDistance = PLANET_DISTANCES[form.destinationPlanet];
    // Calculate the absolute distance between planets
    const distance = Math.abs(destDistance - sourceDistance);

    const {
      travelTime,
      shipTime,
      timeDilation,
      interestRate,
    } = calculateTradeParameters(distance, form.paymentType);

    const baseCost = form.quantity * 100; // Base cost per unit
    const totalCost = baseCost * (1 + interestRate);

    setCalculation({
      distance,
      travelTime,
      shipTime,
      timeDilation,
      interestRate,
      totalCost,
    });

    toast({
      title: 'Trade Calculation Complete',
      description: `Travel time: ${travelTime.toFixed(1)} Earth days (${shipTime.toFixed(1)} ship days)`,
      status: 'success',
      duration: 5000,
      isClosable: true,
    });
  };

  return (
    <Box p={6} bg="white" borderRadius="xl" boxShadow="lg" h="100%" overflowY="auto">
      <VStack spacing={4} align="stretch">
        <Heading size="lg" mb={2}>Interstellar Trade</Heading>

        <HStack spacing={4}>
          <FormControl>
            <FormLabel>Source Location</FormLabel>
            <Select
              value={form.sourcePlanet}
              onChange={(e) => setForm({ ...form, sourcePlanet: e.target.value })}
            >
              {Object.keys(PLANET_DISTANCES).map(planet => (
                <option key={planet} value={planet}>{planet}</option>
              ))}
            </Select>
            {form.sourcePlanet && (
              <Text fontSize="sm" color="gray.600" mt={1}>
                {CELESTIAL_BODY_INFO[form.sourcePlanet]}
              </Text>
            )}
          </FormControl>

          <FormControl>
            <FormLabel>Destination Location</FormLabel>
            <Select
              value={form.destinationPlanet}
              onChange={(e) => setForm({ ...form, destinationPlanet: e.target.value })}
            >
              {Object.keys(PLANET_DISTANCES).map(planet => (
                <option key={planet} value={planet}>{planet}</option>
              ))}
            </Select>
            {form.destinationPlanet && (
              <Text fontSize="sm" color="gray.600" mt={1}>
                {CELESTIAL_BODY_INFO[form.destinationPlanet]}
              </Text>
            )}
          </FormControl>
        </HStack>

        <FormControl>
          <FormLabel>Goods</FormLabel>
          <Input
            value={form.goods}
            onChange={(e) => setForm({ ...form, goods: e.target.value })}
            placeholder="Enter goods description"
          />
        </FormControl>

        <HStack spacing={4}>
          <FormControl>
            <FormLabel>Quantity</FormLabel>
            <Input
              type="number"
              pattern="[0-9]*"
              inputMode="numeric"
              value={form.quantity === 0 ? '' : form.quantity}
              onChange={(e) => {
                const value = e.target.value === '' ? 0 : Math.max(0, parseInt(e.target.value, 10));
                if (!isNaN(value)) {
                  setForm({ ...form, quantity: value });
                }
              }}
              onKeyDown={(e) => {
                if (!/[0-9]|\Backspace|\Tab|\Delete|\ArrowLeft|\ArrowRight/.test(e.key)) {
                  e.preventDefault();
                }
              }}
              placeholder="Enter quantity"
            />
          </FormControl>

          <FormControl>
            <FormLabel>Payment Type</FormLabel>
            <Select
              value={form.paymentType}
              onChange={(e) => setForm({ ...form, paymentType: e.target.value as 'upfront' | 'on_delivery' })}
            >
              <option value="upfront">Pay Upfront</option>
              <option value="on_delivery">Pay on Delivery</option>
            </Select>
          </FormControl>
        </HStack>

        <Button
          colorScheme="blue"
          size="lg"
          onClick={handleSubmit}
          mt={4}
        >
          Calculate Trade
        </Button>

        {calculation && (
          <Box mt={4} p={4} borderRadius="md" bg="gray.50">
            <StatGroup>
              <Stat>
                <StatLabel>Distance</StatLabel>
                <StatNumber>{typeof calculation.distance === 'number' ? calculation.distance.toFixed(2) : '0.00'} AU</StatNumber>
                <StatHelpText>Astronomical Units</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Earth Time</StatLabel>
                <StatNumber>{typeof calculation.travelTime === 'number' ? calculation.travelTime.toFixed(1) : '0.0'} Earth days</StatNumber>
                <StatHelpText>In stationary frame</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Ship Time</StatLabel>
                <StatNumber>{typeof calculation.shipTime === 'number' ? calculation.shipTime.toFixed(1) : '0.0'} days</StatNumber>
                <StatHelpText>Due to time dilation</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Time Dilation</StatLabel>
                <StatNumber>{typeof calculation.timeDilation === 'number' ? calculation.timeDilation.toFixed(2) : '1.00'}x</StatNumber>
                <StatHelpText>Relativistic factor</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Interest Rate</StatLabel>
                <StatNumber>{typeof calculation.interestRate === 'number' ? (calculation.interestRate * 100).toFixed(2) : '0.00'}%</StatNumber>
                <StatHelpText>{form.paymentType === 'upfront' ? 'For upfront payment' : 'For payment on delivery'}</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Total Cost</StatLabel>
                <StatNumber>{typeof calculation.totalCost === 'number' ? calculation.totalCost.toFixed(2) : '0.00'} credits</StatNumber>
                <StatHelpText>Including interest</StatHelpText>
              </Stat>
            </StatGroup>
          </Box>
        )}
      </VStack>
    </Box>
  );
};
