import React, { useState } from 'react';
import {
  Box,
  VStack,
  HStack,
  FormControl,
  FormLabel,
  Input,
  Select,
  Button,
  Text,
  useToast,
  Heading,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  StatGroup,
  ScaleFade,
  Image,
} from '@chakra-ui/react';

interface TradeForm {
  sourcePlanet: string;
  destinationPlanet: string;
  goods: string;
  quantity: number;
  paymentType: 'upfront' | 'on_delivery';
}

// Planet and star system data
// Distances are stored in AU (1 light year = 63241.1 AU)
const PLANET_DISTANCES: Record<string, number> = {
  // Solar System
  Mercury: 0.387,
  Venus: 0.723,
  Earth: 1,
  Mars: 1.524,
  Jupiter: 5.203,
  Saturn: 9.537,
  Uranus: 19.191,
  Neptune: 30.069,

  // Nearby Stars and Their Planets
  'Proxima Centauri b': 268725.8,  // 4.25 light years
  'Alpha Centauri A': 271909.7,    // 4.3 light years
  'Alpha Centauri B': 271909.7,    // 4.3 light years
  'Barnard\'s Star': 379446.6,     // 6.0 light years
  'Wolf 359': 474308.3,            // 7.5 light years
  'Lalande 21185': 515521.8,       // 8.15 light years
  'Sirius A': 537549.4,            // 8.5 light years
  'Sirius B': 537549.4,            // 8.5 light years

  // Notable Exoplanets
  'TRAPPIST-1d': 2529644.0,       // 40 light years
  'TRAPPIST-1e': 2529644.0,       // 40 light years
  'TRAPPIST-1f': 2529644.0,       // 40 light years
  'Kepler-186f': 3099814.0,       // 49 light years
  'TOI-700d': 6324110.0,          // 100 light years

  // Notable Star Systems
  'Betelgeuse': 39525687.5,       // 625 light years
  'Vega': 158102750.0,            // 2500 light years
  'Antares': 316205500.0,         // 5000 light years
  'Deneb': 474308250.0,           // 7500 light years

  // Galactic Features
  'Orion Nebula': 853754850.0,    // 13,500 light years
  'Galactic Center': 1580275000.0, // 25,000 light years
  'LMC': 3162055000.0,            // 50,000 light years (Large Magellanic Cloud)
};

// Update the interface to include descriptions
interface TradeForm {
  sourcePlanet: string;
  destinationPlanet: string;
  goods: string;
  quantity: number;
  paymentType: 'upfront' | 'on_delivery';
}

// Add planet descriptions
const CELESTIAL_BODY_INFO: Record<string, string> = {
  Mercury: 'Innermost planet of the Solar System',
  Venus: 'Second planet from the Sun, similar in size to Earth',
  Earth: 'Our home planet',
  Mars: 'The Red Planet, target for human colonization',
  Jupiter: 'Largest planet in the Solar System',
  Saturn: 'Known for its spectacular ring system',
  Uranus: 'Ice giant with unique sideways rotation',
  Neptune: 'The windiest planet in the Solar System',
  'Proxima Centauri b': 'Closest known exoplanet to Earth, potentially habitable',
  'Alpha Centauri A': 'Part of the closest star system to Earth',
  'Alpha Centauri B': 'Binary companion to Alpha Centauri A',
  'Barnard\'s Star': 'One of the closest stars to Earth',
  'Wolf 359': 'Small red dwarf star in Leo constellation',
  'Lalande 21185': 'Red dwarf with confirmed exoplanets',
  'Sirius A': 'Brightest star in Earth\'s night sky',
  'Sirius B': 'White dwarf companion to Sirius A',
  'TRAPPIST-1d': 'Part of the TRAPPIST-1 system of seven Earth-like planets',
  'TRAPPIST-1e': 'Most promising TRAPPIST-1 planet for habitability',
  'TRAPPIST-1f': 'Water-rich world in the TRAPPIST-1 system',
  'Kepler-186f': 'First Earth-sized planet discovered in habitable zone',
  'TOI-700d': 'Earth-sized planet in its star\'s habitable zone',
  'Betelgeuse': 'Red supergiant star, could go supernova soon',
  'Vega': 'Bright star and former North Star',
  'Antares': 'Red supergiant star in Scorpius',
  'Deneb': 'One of the most luminous known stars',
  'Orion Nebula': 'Closest massive star formation region to Earth',
  'Galactic Center': 'Supermassive black hole at the heart of the Milky Way',
  'LMC': 'Largest satellite galaxy of the Milky Way'
};

// Add planet images data
const PLANET_IMAGES: Record<string, string> = {
  Mercury: 'https://raw.githubusercontent.com/devicons/devicon/master/icons/mercury/mercury-original.svg',
  Venus: 'https://raw.githubusercontent.com/devicons/devicon/master/icons/venus/venus-original.svg',
  Earth: '/planets/earth.png',
  Mars: '/planets/mars.png',
  Jupiter: '/planets/jupiter.png',
  Saturn: '/planets/saturn.png',
  Uranus: '/planets/uranus.png',
  Neptune: '/planets/neptune.png',
  'Proxima Centauri b': '/planets/exoplanet1.png',
  'Alpha Centauri A': '/planets/star1.png',
  'Alpha Centauri B': '/planets/star2.png',
  'Barnard\'s Star': '/planets/star3.png',
  'Wolf 359': '/planets/star4.png',
  'Lalande 21185': '/planets/star5.png',
  'Sirius A': '/planets/star6.png',
  'Sirius B': '/planets/star7.png',
  'TRAPPIST-1d': '/planets/exoplanet2.png',
  'TRAPPIST-1e': '/planets/exoplanet3.png',
  'TRAPPIST-1f': '/planets/exoplanet4.png',
  'Kepler-186f': '/planets/exoplanet5.png',
  'TOI-700d': '/planets/exoplanet6.png',
  'Betelgeuse': '/planets/star8.png',
  'Vega': '/planets/star9.png',
  'Antares': '/planets/star10.png',
  'Deneb': '/planets/star11.png',
  'Orion Nebula': '/planets/nebula1.png',
  'Galactic Center': '/planets/blackhole.png',
  'LMC': '/planets/galaxy1.png'
};

export const TradePanel: React.FC = () => {
  const [form, setForm] = useState<TradeForm>({
    sourcePlanet: 'Earth',
    destinationPlanet: 'Mars',
    goods: '',
    quantity: 0,
    paymentType: 'upfront',
  });

  const [calculation, setCalculation] = useState<{
    distance: number;
    travelTime: number;
    shipTime: number;
    interestRate: number;
    totalCost: number;
    timeDilation: number;
  } | null>(null);

  const [selectedSource, setSelectedSource] = useState(false);
  const [selectedDest, setSelectedDest] = useState(false);

  const toast = useToast();

  const calculateTradeParameters = (distance: number, paymentType: 'upfront' | 'on_delivery') => {
    // Constants
    const SPEED_OF_LIGHT = 299792458; // m/s
    const SHIP_SPEED_RATIO = 0.9; // 90% of light speed
    const SHIP_SPEED = SPEED_OF_LIGHT * SHIP_SPEED_RATIO;
    const AU_TO_METERS = 149597870700; // 1 AU in meters
    const SECONDS_PER_DAY = 86400; // 24 * 60 * 60
    const SECONDS_PER_YEAR = 31557600; // 365.25 * 24 * 60 * 60

    // Calculate distance in meters
    const distanceMeters = distance * AU_TO_METERS;

    // Calculate time dilation factor (γ - gamma)
    // γ = 1 / sqrt(1 - v²/c²)
    const timeDilationFactor = 1 / Math.sqrt(1 - Math.pow(SHIP_SPEED_RATIO, 2));

    // Calculate Earth time (coordinate time) in seconds
    // t = d/v where d is distance and v is ship speed
    const earthTimeSeconds = distanceMeters / SHIP_SPEED;

    // Calculate ship time (proper time) in seconds
    // t' = t/γ where t is Earth time and γ is time dilation factor
    const shipTimeSeconds = earthTimeSeconds / timeDilationFactor;

    // Convert to days
    const earthTimeDays = earthTimeSeconds / SECONDS_PER_DAY;
    const shipTimeDays = shipTimeSeconds / SECONDS_PER_DAY;

    // Calculate interest based on proper time and time dilation
    const BASE_INTEREST_RATE = 0.05; // 5% annual interest rate

    // Calculate effective interest rate based on payment type
    let effectiveInterestRate;
    if (paymentType === 'upfront') {
      // For upfront payment, the money could have been invested on Earth
      // Use Earth time (coordinate time) for interest calculation
      const earthYears = earthTimeSeconds / SECONDS_PER_YEAR;
      effectiveInterestRate = Math.pow(1 + BASE_INTEREST_RATE, earthYears) - 1;
    } else {
      // For payment on delivery, account for time dilation
      // The effective time for interest is the Earth time
      const effectiveYears = earthTimeSeconds / SECONDS_PER_YEAR;
      effectiveInterestRate = Math.pow(1 + BASE_INTEREST_RATE, effectiveYears) - 1;
    }

    // Calculate total cost including interest
    const baseCost = form.quantity * 1000; // Base cost per unit
    const totalCost = baseCost * (1 + effectiveInterestRate);

    return {
      distance,
      travelTime: earthTimeDays,
      shipTime: shipTimeDays,
      timeDilation: timeDilationFactor,
      interestRate: effectiveInterestRate,
      totalCost,
    };
  };

  const handleSubmit = async () => {
    const sourceDistance = PLANET_DISTANCES[form.sourcePlanet];
    const destDistance = PLANET_DISTANCES[form.destinationPlanet];
    const distance = Math.abs(destDistance - sourceDistance);

    const result = calculateTradeParameters(distance, form.paymentType);
    setCalculation(result);

    // Send calculation details to the chat interface
    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: '!explain_calculation:' + JSON.stringify({
            sourcePlanet: form.sourcePlanet,
            destinationPlanet: form.destinationPlanet,
            distance,
            travelTime: result.travelTime,
            shipTime: result.shipTime,
            timeDilation: result.timeDilation,
            interestRate: result.interestRate,
            totalCost: result.totalCost,
            paymentType: form.paymentType,
            goods: form.goods,
            quantity: form.quantity
          })
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to send calculation details');
      }

      toast({
        title: 'Trade Calculation Complete',
        description: 'Calculation details have been sent to the chat.',
        status: 'success',
        duration: 5000,
        isClosable: true,
      });
    } catch (error) {
      console.error('Failed to send calculation details:', error);
      toast({
        title: 'Error',
        description: 'Failed to send calculation details to chat',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  return (
    <Box p={6} bg="white" borderRadius="xl" boxShadow="lg" h="100%" overflowY="auto">
      <VStack spacing={4} align="stretch">
        <Heading size="lg" mb={2}>Interstellar Trade</Heading>

        <HStack spacing={4} align="flex-start">
          <FormControl>
            <FormLabel>Source Location</FormLabel>
            <Box position="relative">
              <Select
                value={form.sourcePlanet}
                onChange={(e) => {
                  setForm({ ...form, sourcePlanet: e.target.value });
                  setSelectedSource(true);
                  setTimeout(() => setSelectedSource(false), 1000);
                }}
              >
                {Object.keys(PLANET_DISTANCES).map(planet => (
                  <option key={planet} value={planet}>{planet}</option>
                ))}
              </Select>
              {form.sourcePlanet && (
                <Box
                  mt={2}
                  position="relative"
                  width="150px"
                  height="150px"
                  overflow="hidden"
                >
                  <ScaleFade in={true} initialScale={0.8}>
                    <Box
                      transition="all 0.5s ease-in-out"
                      transform={selectedSource ? "scale(1.2)" : "scale(1)"}
                    >
                      <Image
                        src={PLANET_IMAGES[form.sourcePlanet]}
                        alt={form.sourcePlanet}
                        width="150px"
                        height="150px"
                        objectFit="cover"
                        borderRadius="full"
                        boxShadow={selectedSource ? "0 0 20px rgba(66, 153, 225, 0.6)" : "none"}
                      />
                    </Box>
                  </ScaleFade>
                  <Text 
                    fontSize="sm" 
                    color="gray.600" 
                    mt={2}
                    textAlign="center"
                  >
                    {CELESTIAL_BODY_INFO[form.sourcePlanet]}
                  </Text>
                </Box>
              )}
            </Box>
          </FormControl>

          <FormControl>
            <FormLabel>Destination Location</FormLabel>
            <Box position="relative">
              <Select
                value={form.destinationPlanet}
                onChange={(e) => {
                  setForm({ ...form, destinationPlanet: e.target.value });
                  setSelectedDest(true);
                  setTimeout(() => setSelectedDest(false), 1000);
                }}
              >
                {Object.keys(PLANET_DISTANCES).map(planet => (
                  <option key={planet} value={planet}>{planet}</option>
                ))}
              </Select>
              {form.destinationPlanet && (
                <Box
                  mt={2}
                  position="relative"
                  width="150px"
                  height="150px"
                  overflow="hidden"
                >
                  <ScaleFade in={true} initialScale={0.8}>
                    <Box
                      transition="all 0.5s ease-in-out"
                      transform={selectedDest ? "scale(1.2)" : "scale(1)"}
                    >
                      <Image
                        src={PLANET_IMAGES[form.destinationPlanet]}
                        alt={form.destinationPlanet}
                        width="150px"
                        height="150px"
                        objectFit="cover"
                        borderRadius="full"
                        boxShadow={selectedDest ? "0 0 20px rgba(66, 153, 225, 0.6)" : "none"}
                      />
                    </Box>
                  </ScaleFade>
                  <Text 
                    fontSize="sm" 
                    color="gray.600" 
                    mt={2}
                    textAlign="center"
                  >
                    {CELESTIAL_BODY_INFO[form.destinationPlanet]}
                  </Text>
                </Box>
              )}
            </Box>
          </FormControl>
        </HStack>

        <FormControl>
          <FormLabel>Goods</FormLabel>
          <Input
            value={form.goods}
            onChange={(e) => setForm({ ...form, goods: e.target.value })}
            placeholder="Enter goods description"
          />
        </FormControl>

        <HStack spacing={4}>
          <FormControl>
            <FormLabel>Quantity</FormLabel>
            <Input
              type="number"
              min="1"
              value={form.quantity}
              onChange={(e) => setForm({ ...form, quantity: Math.max(1, Number(e.target.value)) })}
            />
          </FormControl>

          <FormControl>
            <FormLabel>Payment Type</FormLabel>
            <Select
              value={form.paymentType}
              onChange={(e) => setForm({ ...form, paymentType: e.target.value as 'upfront' | 'on_delivery' })}
            >
              <option value="upfront">Pay Upfront</option>
              <option value="on_delivery">Pay on Delivery</option>
            </Select>
          </FormControl>
        </HStack>

        <Button
          colorScheme="blue"
          size="lg"
          onClick={handleSubmit}
          mt={4}
        >
          Calculate Trade
        </Button>

        {calculation && (
          <Box mt={4} p={4} borderRadius="md" bg="gray.50">
            <StatGroup>
              <Stat>
                <StatLabel>Distance</StatLabel>
                <StatNumber>{calculation.distance.toFixed(2)} AU</StatNumber>
                <StatHelpText>Astronomical Units</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Earth Time</StatLabel>
                <StatNumber>{calculation.travelTime.toFixed(2)} days</StatNumber>
                <StatHelpText>In stationary frame</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Ship Time</StatLabel>
                <StatNumber>{calculation.shipTime.toFixed(2)} days</StatNumber>
                <StatHelpText>Due to time dilation</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Time Dilation</StatLabel>
                <StatNumber>{calculation.timeDilation.toFixed(4)}x</StatNumber>
                <StatHelpText>Relativistic factor</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Interest Rate</StatLabel>
                <StatNumber>{(calculation.interestRate * 100).toFixed(4)}%</StatNumber>
                <StatHelpText>{form.paymentType === 'upfront' ? 'For upfront payment' : 'For payment on delivery'}</StatHelpText>
              </Stat>
              <Stat>
                <StatLabel>Total Cost</StatLabel>
                <StatNumber>{calculation.totalCost.toFixed(2)} credits</StatNumber>
                <StatHelpText>Including interest</StatHelpText>
              </Stat>
            </StatGroup>
          </Box>
        )}
      </VStack>
    </Box>
  );
};
