export const TradePanel: React.FC = () => {
  const [form, setForm] = useState<TradeForm>({
    sourcePlanet: 'Earth',
    destinationPlanet: 'Mars',
    goods: '',
    quantity: 1,
    paymentType: 'upfront',
  });

  const [calculation, setCalculation] = useState<{
    distance: number;
    travelTime: number;
    shipTime: number;
    interestRate: number;
    totalCost: number;
    timeDilation: number;
  } | null>(null);

  const [isFormActive, setIsFormActive] = useState(false);

  const toast = useToast();

  const distance = useMemo(() => {
    const sourceDistance = PLANET_DISTANCES[form.sourcePlanet];
    const destDistance = PLANET_DISTANCES[form.destinationPlanet];
    return Math.abs(destDistance - sourceDistance);
  }, [form.sourcePlanet, form.destinationPlanet]);

  const calculateTradeParameters = (distance: number, paymentType: 'upfront' | 'on_delivery') => {
    // Constants
    const SPEED_OF_LIGHT = 299792.458; // km/s
    const SHIP_SPEED_RATIO = 0.0001; // 0.01% of light speed (about 107,925 km/h)
    const SHIP_SPEED = SPEED_OF_LIGHT * SHIP_SPEED_RATIO; // km/s
    const AU_TO_KM = 149597870.7; // 1 AU in km
    const SECONDS_PER_DAY = 86400; // 24 * 60 * 60
    const DAYS_PER_YEAR = 365.25;

    const distanceKm = distance * AU_TO_KM;
    const timeDilationFactor = 1 / Math.sqrt(1 - Math.pow(SHIP_SPEED_RATIO, 2));
    const earthTimeSeconds = distanceKm / SHIP_SPEED;
    const shipTimeSeconds = earthTimeSeconds / timeDilationFactor;
    const earthTimeDays = earthTimeSeconds / SECONDS_PER_DAY;
    const shipTimeDays = shipTimeSeconds / SECONDS_PER_DAY;
    const earthYears = earthTimeDays / DAYS_PER_YEAR;

    const BASE_INTEREST_RATE = 0.05; // 5% annual interest rate
    const effectiveInterestRate = paymentType === 'upfront'
      ? Math.pow(1 + BASE_INTEREST_RATE, earthYears) - 1
      : Math.pow(1 + BASE_INTEREST_RATE, earthYears * timeDilationFactor) - 1;

    const baseCost = Math.max(1, form.quantity) * 1000;
    const totalCost = baseCost * (1 + effectiveInterestRate);

    return {
      distance: parseFloat(distance.toFixed(2)),
      travelTime: parseFloat(earthTimeDays.toFixed(2)),
      shipTime: parseFloat(shipTimeDays.toFixed(2)),
      timeDilation: parseFloat(timeDilationFactor.toFixed(4)),
      interestRate: effectiveInterestRate,
      totalCost: parseFloat(totalCost.toFixed(2))
    };
  };

  const handleSubmit = async () => {
    if (form.quantity <= 0) {
      toast({
        title: 'Invalid Quantity',
        description: 'Please enter a quantity greater than 0',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
      return;
    }

    const result = calculateTradeParameters(distance, form.paymentType);
    setCalculation(result);
  };

  return (
    <Box p={6} bg="white" borderRadius="xl" boxShadow="lg" h="100%" overflowY="auto">
      <GameFont />
      <VStack spacing={4} align="stretch">
        <Box
          transform={isFormActive ? "scale(1.02)" : "scale(1)"}
          transition="all 0.3s ease-in-out"
          bg={isFormActive ? "white" : "transparent"}
          p={4}
          borderRadius="xl"
          boxShadow={isFormActive ? "lg" : "none"}
          onClick={() => setIsFormActive(true)}
          onBlur={(e) => {
            if (!e.currentTarget.contains(e.relatedTarget)) {
              setIsFormActive(false);
            }
          }}
          tabIndex={0}
        >
          <VStack spacing={4} align="stretch">
            <Heading size="lg">Interstellar Trade</Heading>

            {/* Planet Visualization */}
            <Box 
              w="100%" 
              h={isFormActive ? "100px" : "150px"}
              bg="gray.900" 
              borderRadius="xl" 
              position="relative"
              overflow="hidden"
              transition="all 0.3s ease-in-out"
              transform={isFormActive ? "scale(0.95)" : "scale(1)"}
            >
              {/* Stars background effect */}
              <Box
                position="absolute"
                top={0}
                left={0}
                right={0}
                bottom={0}
                bgImage="radial-gradient(white 1px, transparent 1px)"
                backgroundSize="15px 15px"
                opacity={0.2}
              />

              {/* Planets display */}
              <Flex
                h="100%"
                justify="space-around"
                align="center"
                position="relative"
                px={8}
              >
                {/* Source Planet */}
                <VStack spacing={2}>
                  <Circle
                    size="30px"
                    bg="cyan.400"
                    boxShadow="0 0 20px rgba(255,255,255,0.2)"
                    border="2px solid rgba(255,255,255,0.2)"
                  />
                  <Text 
                    color="white" 
                    fontSize="xs" 
                    fontWeight="medium"
                    sx={{
                      fontFamily: "'Press Start 2P', cursive",
                      textShadow: "0 0 5px rgba(0,255,255,0.5)",
                      letterSpacing: "-0.05em"
                    }}
                  >
                    {form.sourcePlanet}
                  </Text>
                </VStack>

                {/* Distance Line */}
                <VStack>
                  <svg width="200" height="2" style={{ overflow: 'visible' }}>
                    <line
                      x1="0"
                      y1="0"
                      x2="200"
                      y2="0"
                      stroke="cyan"
                      strokeWidth="2"
                      strokeDasharray="4"
                      style={{
                        animation: 'flow 1s linear infinite',
                        filter: 'drop-shadow(0 0 8px cyan)',
                      }}
                    />
                  </svg>
                  <Text
                    color="cyan.200"
                    fontSize="xs"
                    mt={-2}
                    sx={{
                      fontFamily: "'Press Start 2P', cursive",
                      textShadow: "0 0 5px rgba(0,255,255,0.5)",
                    }}
                  >
                    {distance < 1000 
                      ? `${distance.toFixed(2)} AU`
                      : `${(distance / 63241.1).toFixed(2)} LY`}
                  </Text>
                </VStack>

                {/* Destination Planet */}
                <VStack spacing={2}>
                  <Circle
                    size="30px"
                    bg="cyan.400"
                    boxShadow="0 0 20px rgba(255,255,255,0.2)"
                    border="2px solid rgba(255,255,255,0.2)"
                  />
                  <Text 
                    color="white" 
                    fontSize="xs" 
                    fontWeight="medium"
                    sx={{
                      fontFamily: "'Press Start 2P', cursive",
                      textShadow: "0 0 5px rgba(0,255,255,0.5)",
                      letterSpacing: "-0.05em"
                    }}
                  >
                    {form.destinationPlanet}
                  </Text>
                </VStack>
              </Flex>
            </Box>

            {/* Form Controls */}
            <HStack spacing={4}>
              <FormControl>
                <FormLabel>Source Location</FormLabel>
                <Select
                  value={form.sourcePlanet}
                  onChange={(e) => {
                    setForm({ ...form, sourcePlanet: e.target.value });
                    setIsFormActive(true);
                  }}
                  onFocus={() => setIsFormActive(true)}
                >
                  {Object.keys(PLANET_DISTANCES).map(planet => (
                    <option key={planet} value={planet}>{planet}</option>
                  ))}
                </Select>
              </FormControl>

              <FormControl>
                <FormLabel>Destination Location</FormLabel>
                <Select
                  value={form.destinationPlanet}
                  onChange={(e) => {
                    setForm({ ...form, destinationPlanet: e.target.value });
                    setIsFormActive(true);
                  }}
                  onFocus={() => setIsFormActive(true)}
                >
                  {Object.keys(PLANET_DISTANCES).map(planet => (
                    <option key={planet} value={planet}>{planet}</option>
                  ))}
                </Select>
              </FormControl>
            </HStack>

            <FormControl>
              <FormLabel>Goods</FormLabel>
              <Input
                value={form.goods}
                onChange={(e) => {
                  setForm({ ...form, goods: e.target.value });
                  setIsFormActive(true);
                }}
                onFocus={() => setIsFormActive(true)}
                placeholder="Enter goods description"
              />
            </FormControl>

            <HStack spacing={4}>
              <FormControl>
                <FormLabel>Quantity</FormLabel>
                <Input
                  type="number"
                  min="1"
                  value={form.quantity}
                  onChange={(e) => {
                    setForm({ ...form, quantity: Math.max(1, Number(e.target.value)) });
                    setIsFormActive(true);
                  }}
                  onFocus={() => setIsFormActive(true)}
                />
              </FormControl>

              <FormControl>
                <FormLabel>Payment Type</FormLabel>
                <Select
                  value={form.paymentType}
                  onChange={(e) => {
                    setForm({ ...form, paymentType: e.target.value as 'upfront' | 'on_delivery' });
                    setIsFormActive(true);
                  }}
                  onFocus={() => setIsFormActive(true)}
                >
                  <option value="upfront">Pay Upfront</option>
                  <option value="on_delivery">Pay on Delivery</option>
                </Select>
              </FormControl>
            </HStack>

            <Button
              colorScheme="blue"
              size="lg"
              onClick={handleSubmit}
              onFocus={() => setIsFormActive(true)}
            >
              Calculate Trade
            </Button>

            {calculation && (
              <Box mt={4} p={4} borderRadius="md" bg="gray.50">
                <StatGroup>
                  <Stat>
                    <StatLabel>Distance</StatLabel>
                    <StatNumber>{calculation.distance.toFixed(2)} AU</StatNumber>
                    <StatHelpText>Astronomical Units</StatHelpText>
                  </Stat>
                  <Stat>
                    <StatLabel>Earth Time</StatLabel>
                    <StatNumber>{calculation.travelTime.toFixed(2)} days</StatNumber>
                    <StatHelpText>In stationary frame</StatHelpText>
                  </Stat>
                  <Stat>
                    <StatLabel>Ship Time</StatLabel>
                    <StatNumber>{calculation.shipTime.toFixed(2)} days</StatNumber>
                    <StatHelpText>Due to time dilation</StatHelpText>
                  </Stat>
                  <Stat>
                    <StatLabel>Time Dilation</StatLabel>
                    <StatNumber>{calculation.timeDilation.toFixed(4)}x</StatNumber>
                    <StatHelpText>Relativistic factor</StatHelpText>
                  </Stat>
                  <Stat>
                    <StatLabel>Interest Rate</StatLabel>
                    <StatNumber>{(calculation.interestRate * 100).toFixed(4)}%</StatNumber>
                    <StatHelpText>{form.paymentType === 'upfront' ? 'For upfront payment' : 'For payment on delivery'}</StatHelpText>
                  </Stat>
                  <Stat>
                    <StatLabel>Total Cost</StatLabel>
                    <StatNumber>{calculation.totalCost.toFixed(2)} credits</StatNumber>
                    <StatHelpText>Including interest</StatHelpText>
                  </Stat>
                </StatGroup>
              </Box>
            )}
          </VStack>
        </Box>
      </VStack>
    </Box>
  );
};
