export const TradePanel: React.FC = () => {
  const [form, setForm] = useState<TradeForm>({
    sourcePlanet: 'Earth',
    destinationPlanet: 'Mars',
    goods: '',
    quantity: 0,
    paymentType: 'upfront',
  });

  const [calculation, setCalculation] = useState<{
    distance: number;
    travelTime: number;
    shipTime: number;
    interestRate: number;
    totalCost: number;
    timeDilation: number;
  } | null>(null);

  const [selectedSource, setSelectedSource] = useState(false);
  const [selectedDest, setSelectedDest] = useState(false);

  const toast = useToast();

  const calculateTradeParameters = (distance: number, paymentType: 'upfront' | 'on_delivery') => {
    // Constants
    const SPEED_OF_LIGHT = 299792458; // m/s
    const SHIP_SPEED_RATIO = 0.1; // 10% of light speed - more realistic for near-future space travel
    const SHIP_SPEED = SPEED_OF_LIGHT * SHIP_SPEED_RATIO;
    const AU_TO_METERS = 149597870700; // 1 AU in meters
    const SECONDS_PER_DAY = 86400; // 24 * 60 * 60
    const SECONDS_PER_YEAR = 31557600; // 365.25 * 24 * 60 * 60
    const BASE_INTEREST_RATE = 0.05; // 5% annual interest rate

    // Calculate distance in meters
    const distanceMeters = distance * AU_TO_METERS;

    // Calculate time dilation factor (γ - gamma)
    // γ = 1 / sqrt(1 - v²/c²)
    const timeDilationFactor = 1 / Math.sqrt(1 - Math.pow(SHIP_SPEED_RATIO, 2));

    // Calculate Earth time (coordinate time) in seconds
    // t = d/v where d is distance and v is ship speed
    const earthTimeSeconds = distanceMeters / SHIP_SPEED;

    // Calculate ship time (proper time) in seconds
    // t' = t/γ where t is Earth time and γ is time dilation factor
    const shipTimeSeconds = earthTimeSeconds / timeDilationFactor;

    // Convert to days
    const earthTimeDays = earthTimeSeconds / SECONDS_PER_DAY;
    const shipTimeDays = shipTimeSeconds / SECONDS_PER_DAY;

    // Calculate years for interest
    const earthYears = earthTimeSeconds / SECONDS_PER_YEAR;

    // Calculate interest rates based on Krugman's paper
    let effectiveInterestRate;
    if (paymentType === 'upfront') {
      // For upfront payment, calculate compound interest over Earth time
      // The money could have been invested on Earth during the travel time
      effectiveInterestRate = Math.pow(1 + BASE_INTEREST_RATE, earthYears) - 1;
    } else {
      // For payment on delivery, the seller needs to be compensated for both
      // the time value of money and relativistic effects
      const effectiveYears = earthYears * timeDilationFactor;
      effectiveInterestRate = Math.pow(1 + BASE_INTEREST_RATE, effectiveYears) - 1;
    }

    // Calculate total cost
    const baseCost = form.quantity * 1000; // Base cost per unit
    const totalCost = baseCost * (1 + effectiveInterestRate);

    // For debugging
    console.log({
      distance: `${distance.toFixed(3)} AU = ${(distanceMeters/1000).toFixed(0)} km`,
      shipSpeed: `${(SHIP_SPEED/1000).toFixed(0)} km/s = ${(SHIP_SPEED/SPEED_OF_LIGHT*100).toFixed(1)}% of c`,
      earthTime: `${earthTimeDays.toFixed(2)} days`,
      shipTime: `${shipTimeDays.toFixed(2)} days`,
      timeDilation: timeDilationFactor.toFixed(4),
      interestRate: `${(effectiveInterestRate * 100).toFixed(4)}%`,
      totalCost: totalCost.toFixed(2)
    });

    return {
      distance,
      travelTime: earthTimeDays,
      shipTime: shipTimeDays,
      timeDilation: timeDilationFactor,
      interestRate: effectiveInterestRate,
      totalCost,
    };
  };

  const handleSubmit = () => {
    const sourceDistance = PLANET_DISTANCES[form.sourcePlanet];
    const destDistance = PLANET_DISTANCES[form.destinationPlanet];
    const distance = Math.abs(destDistance - sourceDistance);

    const result = calculateTradeParameters(distance, form.paymentType);
    setCalculation(result);

    // Show detailed calculation results in toast
    const message = `
Distance: ${distance.toFixed(2)} AU
Travel time: ${result.travelTime.toFixed(1)} Earth days
Ship time: ${result.shipTime.toFixed(1)} ship days
Interest rate: ${(result.interestRate * 100).toFixed(4)}%
    `;

    toast({
      title: 'Trade Calculation Complete',
      description: message,
      status: 'success',
      duration: 5000,
      isClosable: true,
    });
  };

  const getCalculationExplanation = (calculation: any) => {
    if (!calculation) return '';

    return `Calculation Explanation:

Distance: ${calculation.distance.toFixed(2)} AU (${(calculation.distance * 149597870.7).toFixed(0)} million km)

Travel Time Calculation:
• Ship speed: 10% of light speed (29,979 km/s)
• Earth observer time: ${calculation.travelTime.toFixed(1)} days
• Ship crew time: ${calculation.shipTime.toFixed(1)} days
• Time dilation factor: ${calculation.timeDilation.toFixed(4)}

According to Krugman's "Theory of Interstellar Trade" (1978):
1. Interest rates must be calculated in proper time (ship time) for the trader's reference frame
2. Different time rates create arbitrage opportunities
3. Time dilation affects both the goods' delivery and the interest payments

For ${form.paymentType === 'upfront' ? 'upfront payment' : 'payment on delivery'}:
• Base annual interest rate: 5%
• Effective interest rate: ${(calculation.interestRate * 100).toFixed(4)}%
• Total cost: ${calculation.totalCost.toFixed(2)} credits

The calculations follow Einstein's special relativity:
• Time dilation: t' = t/γ where γ = 1/√(1 - v²/c²)
• At 10% light speed, time dilation is small but measurable
• Earth time > Ship time due to relativistic effects`;
  };

  return (
    <Box p={6} bg="white" borderRadius="xl" boxShadow="lg" h="100%" overflowY="auto">
      <VStack spacing={6} align="stretch">
        <Heading size="lg">Interstellar Trade</Heading>

        {/* Planet Visualization */}
        <HStack justify="center" spacing={8} py={4}>
          <Box position="relative" textAlign="center" w={CELESTIAL_IMAGES[form.sourcePlanet]?.size || 80}>
            <Box
              position="relative"
              transition="all 0.3s ease-in-out"
              transform={selectedSource ? "scale(1.3)" : "scale(1)"}
              zIndex={selectedSource ? 2 : 1}
            >
              <Image
                src={CELESTIAL_IMAGES[form.sourcePlanet]?.imageUrl || '/default-planet.png'}
                alt={form.sourcePlanet}
                w="100%"
                h="100%"
                objectFit="contain"
                filter="drop-shadow(0 0 8px rgba(0,0,0,0.2))"
                transition="all 0.3s ease-in-out"
                _hover={{ filter: "drop-shadow(0 0 12px rgba(0,0,0,0.3))" }}
              />
              <Box
                position="absolute"
                top="-30px"
                left="50%"
                transform="translateX(-50%)"
                bg="gray.800"
                color="white"
                px={3}
                py={1}
                borderRadius="full"
                fontSize="sm"
                whiteSpace="nowrap"
                boxShadow="lg"
                opacity="0.9"
              >
                {form.sourcePlanet}
              </Box>
            </Box>
          </Box>

          {/* Arrow between planets */}
          <Box fontSize="24px" color="blue.500" display="flex" alignItems="center">
            <Box as="span" fontSize="32px" color="blue.400">⟶</Box>
          </Box>

          <Box position="relative" textAlign="center" w={CELESTIAL_IMAGES[form.destinationPlanet]?.size || 80}>
            <Box
              position="relative"
              transition="all 0.3s ease-in-out"
              transform={selectedDest ? "scale(1.3)" : "scale(1)"}
              zIndex={selectedDest ? 2 : 1}
            >
              <Image
                src={CELESTIAL_IMAGES[form.destinationPlanet]?.imageUrl || '/default-planet.png'}
                alt={form.destinationPlanet}
                w="100%"
                h="100%"
                objectFit="contain"
                filter="drop-shadow(0 0 8px rgba(0,0,0,0.2))"
                transition="all 0.3s ease-in-out"
                _hover={{ filter: "drop-shadow(0 0 12px rgba(0,0,0,0.3))" }}
              />
              <Box
                position="absolute"
                top="-30px"
                left="50%"
                transform="translateX(-50%)"
                bg="gray.800"
                color="white"
                px={3}
                py={1}
                borderRadius="full"
                fontSize="sm"
                whiteSpace="nowrap"
                boxShadow="lg"
                opacity="0.9"
              >
                {form.destinationPlanet}
              </Box>
            </Box>
          </Box>
        </HStack>

        {/* Trade Form Panel */}
        <Box 
          p={5} 
          bg="gray.50" 
          borderRadius="lg" 
          border="1px" 
          borderColor="gray.200"
        >
          <VStack spacing={4} align="stretch">
            <HStack spacing={4} alignItems="flex-start">
              <FormControl flex={1}>
                <FormLabel>Source Location</FormLabel>
                <Select
                  value={form.sourcePlanet}
                  onChange={(e) => setForm({ ...form, sourcePlanet: e.target.value })}
                >
                  {Object.keys(PLANET_DISTANCES).map(planet => (
                    <option key={planet} value={planet}>{planet}</option>
                  ))}
                </Select>
                {form.sourcePlanet && (
                  <ScaleFade in={true} initialScale={0.8}>
                    <Box
                      transition="all 0.5s ease-in-out"
                      transform={selectedSource ? "scale(1.2)" : "scale(1)"}
                      bg={selectedSource ? "blue.50" : "transparent"}
                      p={2}
                      borderRadius="md"
                    >
                      <Text 
                        fontSize="sm" 
                        color={selectedSource ? "blue.600" : "gray.600"} 
                        mt={1}
                        fontWeight={selectedSource ? "medium" : "normal"}
                      >
                        {CELESTIAL_BODY_INFO[form.sourcePlanet]}
                      </Text>
                    </Box>
                  </ScaleFade>
                )}
              </FormControl>

              <FormControl flex={1}>
                <FormLabel>Destination Location</FormLabel>
                <Select
                  value={form.destinationPlanet}
                  onChange={(e) => setForm({ ...form, destinationPlanet: e.target.value })}
                >
                  {Object.keys(PLANET_DISTANCES).map(planet => (
                    <option key={planet} value={planet}>{planet}</option>
                  ))}
                </Select>
                {form.destinationPlanet && (
                  <ScaleFade in={true} initialScale={0.8}>
                    <Box
                      transition="all 0.5s ease-in-out"
                      transform={selectedDest ? "scale(1.2)" : "scale(1)"}
                      bg={selectedDest ? "blue.50" : "transparent"}
                      p={2}
                      borderRadius="md"
                    >
                      <Text 
                        fontSize="sm" 
                        color={selectedDest ? "blue.600" : "gray.600"} 
                        mt={1}
                        fontWeight={selectedDest ? "medium" : "normal"}
                      >
                        {CELESTIAL_BODY_INFO[form.destinationPlanet]}
                      </Text>
                    </Box>
                  </ScaleFade>
                )}
              </FormControl>
            </HStack>

            <FormControl>
              <FormLabel>Goods</FormLabel>
              <Input
                value={form.goods}
                onChange={(e) => setForm({ ...form, goods: e.target.value })}
                placeholder="Enter goods description"
              />
            </FormControl>

            <HStack spacing={4}>
              <FormControl flex={1}>
                <FormLabel>Quantity</FormLabel>
                <Input
                  type="number"
                  min="1"
                  value={form.quantity}
                  onChange={(e) => setForm({ ...form, quantity: Math.max(1, Number(e.target.value)) })}
                />
              </FormControl>

              <FormControl flex={1}>
                <FormLabel>Payment Type</FormLabel>
                <Select
                  value={form.paymentType}
                  onChange={(e) => setForm({ ...form, paymentType: e.target.value as 'upfront' | 'on_delivery' })}
                >
                  <option value="upfront">Pay Upfront</option>
                  <option value="on_delivery">Pay on Delivery</option>
                </Select>
              </FormControl>
            </HStack>

            <Button
              colorScheme="blue"
              size="lg"
              onClick={handleSubmit}
              mt={2}
            >
              Calculate Trade
            </Button>
          </VStack>
        </Box>

        {/* Results Panel */}
        {calculation && (
          <Box 
            p={5} 
            bg="gray.50" 
            borderRadius="lg" 
            border="1px" 
            borderColor="gray.200"
            mt={4}
          >
            <VStack spacing={4} align="stretch">
              <StatGroup gap={4}>
                <Stat>
                  <StatLabel fontWeight="medium">Distance</StatLabel>
                  <StatNumber fontSize="2xl">{calculation.distance.toFixed(2)} AU</StatNumber>
                  <StatHelpText>Astronomical Units</StatHelpText>
                </Stat>
                <Stat>
                  <StatLabel fontWeight="medium">Earth Time</StatLabel>
                  <StatNumber fontSize="2xl">{calculation.travelTime.toFixed(2)} days</StatNumber>
                  <StatHelpText>In stationary frame</StatHelpText>
                </Stat>
                <Stat>
                  <StatLabel fontWeight="medium">Ship Time</StatLabel>
                  <StatNumber fontSize="2xl">{calculation.shipTime.toFixed(2)} days</StatNumber>
                  <StatHelpText>Due to time dilation</StatHelpText>
                </Stat>
                <Stat>
                  <StatLabel fontWeight="medium">Time Dilation</StatLabel>
                  <StatNumber fontSize="2xl">{calculation.timeDilation.toFixed(4)}x</StatNumber>
                  <StatHelpText>Relativistic factor</StatHelpText>
                </Stat>
                <Stat>
                  <StatLabel fontWeight="medium">Interest Rate</StatLabel>
                  <StatNumber fontSize="2xl">{(calculation.interestRate * 100).toFixed(4)}%</StatNumber>
                  <StatHelpText>{form.paymentType === 'upfront' ? 'For upfront payment' : 'For payment on delivery'}</StatHelpText>
                </Stat>
                <Stat>
                  <StatLabel fontWeight="medium">Total Cost</StatLabel>
                  <StatNumber fontSize="2xl">{calculation.totalCost.toFixed(2)} credits</StatNumber>
                  <StatHelpText>Including interest</StatHelpText>
                </Stat>
              </StatGroup>

              {/* Explanation Panel */}
              <Box
                mt={4}
                p={4}
                bg="blue.50"
                borderRadius="md"
                border="1px"
                borderColor="blue.200"
              >
                <Text fontSize="lg" fontWeight="medium" mb={3} color="blue.800">
                  Calculation Details
                </Text>
                <Text
                  whiteSpace="pre-wrap"
                  fontFamily="mono"
                  fontSize="sm"
                  color="blue.900"
                >
                  {getCalculationExplanation(calculation)}
                </Text>
              </Box>
            </VStack>
          </Box>
        )}
      </VStack>
    </Box>
  );
};
