import React, { useState } from 'react';
import {
  Box,
  VStack,
  Input,
  Button,
  Text,
  useToast,
  Flex,
} from '@chakra-ui/react';

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

export const ChatInterface: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const toast = useToast();

  const handleSend = async () => {
    if (!input.trim()) return;

    // Add user message
    const userMessage: Message = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    try {
      // TODO: Implement actual RAG API call
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: input }),
      });

      const data = await response.json();
      const assistantMessage: Message = { role: 'assistant', content: data.response };
      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to get response from the assistant',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  return (
    <Box p={4} bg="white" borderRadius="lg" boxShadow="md" h="100%">
      <VStack spacing={4} h="100%">
        <Text fontSize="xl" fontWeight="bold">Planet Information</Text>

        <Box flex={1} w="100%" overflowY="auto">
          {messages.map((message, index) => (
            <Flex
              key={index}
              justify={message.role === 'user' ? 'flex-end' : 'flex-start'}
              mb={2}
            >
              <Box
                bg={message.role === 'user' ? 'blue.100' : 'gray.100'}
                p={2}
                borderRadius="lg"
                maxW="80%"
              >
                <Text>{message.content}</Text>
              </Box>
            </Flex>
          ))}
        </Box>

        <Flex w="100%" gap={2}>
          <Input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask about planets..."
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          />
          <Button colorScheme="blue" onClick={handleSend}>
            Send
          </Button>
        </Flex>
      </VStack>
    </Box>
  );
};
